import React, { useState } from 'react';
import './App.css';
import AddressForm from './components/AddressForm';
import ResultsDisplay from './components/ResultsDisplay';
import MapComponent from './components/MapComponent';
import { analyzeAddress } from './api/addressService';
import { AddressAnalysisResponse } from './types/address-analysis';

const App: React.FC = () => {
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [results, setResults] = useState<AddressAnalysisResponse | null>(null);

  const handleSearch = async (address: string) => {
    setIsLoading(true);
    setError(null);

    try {
      const data = await analyzeAddress(address);
      setResults(data);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'An error occurred');
      setResults(null);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Ottawa Real Estate Analyzer</h1>
      </header>

      <AddressForm onSearch={handleSearch} isLoading={isLoading} />

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      {isLoading && (
        <div className="loading">
          <p>Analyzing address... This may take a moment</p>
        </div>
      )}

      {results && (
        <>
          <MapComponent 
            latitude={results.coordinates.lat} 
            longitude={results.coordinates.lng} 
            address={results.address}
          />
          <ResultsDisplay results={results} />
        </>
      )}
    </div>
  );
};

export default App;
